vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to -1.0 to 1.0 range
    vec2 st = uv * 2.0 - 1.0;

    // Create flowing sine wave pattern with time offset
    float time = iTime * 0.5;
    float waveX = sin(st.x * 4.0 + time) * 0.5;
    float waveY = sin(st.y * 4.0 + time * 0.7) * 0.5;

    // Combine waves and add noise for complexity
    vec2 flow = vec2(waveX, waveY);
    flow += noise(vec3(st * 4.0 + time, time)) * 0.3;

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        sin(time * 0.5 + st.x * 2.0) * 0.5 + 0.5,
        0.7,
        0.8
    );

    // Convert to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    color *= turbulent(vec3(st * 2.0, time)) * 0.5 + 0.5;

    // Final RGB shift based on flow
    vec3 finalColor = vec3(
        color.r + sin(time + st.x) * 0.3,
        color.g + cos(time + st.y) * 0.3,
        color.b + sin(time * 0.7 + st.x + st.y) * 0.3
    );

    return vec4(finalColor, 1.0);
}
