vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns with time-based animation
    vec2 st = uv * 5.0;
    float time = iTime * 0.5;

    // Add noise to create organic movement
    float n1 = noise(st + vec2(time, 0.0)) * 0.5;
    float n2 = noise(st + vec2(-time, 0.0)) * 0.5;

    // Create undulating patterns using sine waves
    float pattern = sin(st.x * 0.5 + time) + cos(st.y * 0.5 - time);
    pattern += turbulent(st + vec2(time * 0.5, 0.0)) * 0.3;

    // Generate color gradients with shifting hues
    vec3 color1 = hsl2rgb(vec3(0.6 + time * 0.1, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.3 - time * 0.1, 0.8, 0.5));

    // Mix colors based on pattern and noise values
    vec3 finalColor = mix(color1, color2, abs(pattern * 0.5 + n1 * 0.3));

    // Add metallic sheen using noise and turbulence
    float sheen = turbulent(vec3(st.x, st.y, time)) * 0.3;
    finalColor += vec3(0.1) + sheen * 0.2;

    return vec4(finalColor, 1.0);
}
