vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 0.5 + 0.5;
    float r = length(st - 0.5);
    float theta = atan2(st.y - 0.5, st.x - 0.5);
    // Create pulsating effect using time
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    r *= (1.0 + pulse * 0.3);

    // Generate color transitions
    vec3 color1 = hsl2rgb(vec3(0.6 + iTime * 0.1, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.3 + iTime * 0.15, 1.0, 0.7));

    // Add noise texture
    float noiseVal = noise(vec3(st * 4.0, iTime)) * 0.5;
    color1 += noiseVal;
    color2 += noiseVal;
    // Create geometric pattern using polar coordinates
    float shape = sin(theta * 6.0 + iTime) * 0.5 + 0.5;
    shape *= smoothstep(0.3, 0.7, r);
    // Mix colors based on position and time
    vec3 finalColor = mix(color1, color2, shape);

    return vec4(finalColor, 1.0);
}
