vec4 Effect2(vec2 uv) {
    // Compute two different turbulence patterns
    float t = turbulent(uv * 100.0 + vec2(iTime));
    vec3 p = vec3(uv * 50.0, iTime * 0.5);
    float s = turbulent(p);

    // Calculate a variation based on the product of the two turbulence values
    float variation = t * s * 0.1;

    // Generate a base color in HSL space with time-dependent hue
    vec3 hslColor = vec3(iTime * 0.5, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Modulate each RGB channel with the turbulence values and variation
    vec3 finalColor = vec3(
        clamp(rgbColor.r + t * 0.5, 0.0, 1.0),
        clamp(rgbColor.g + s * 0.5, 0.0, 1.0),
        clamp(rgbColor.b + variation * 0.5, 0.0, 1.0)
    );

    return vec4(finalColor, 1.0);
}
