vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 centered = uv - 0.5;

    // Calculate distance from center
    float r = length(centered);

    // Create pulsating circles using time
    float circle = r + sin(iTime) * 0.5 + iTime * 0.5;

    // Generate color with shifting hues
    vec3 color = hsl2rgb(vec3(
        (circle * 10.0 + iTime) * 0.1,   // Hue
        0.7,                              // Saturation
        0.8                               // Lightness
    ));

    // Create pulsating alpha based on distance and time
    float alpha = sin(circle * pi);

    return vec4(color, alpha);
}
