vec4 Effect2(vec2 uv) {
    vec3 color = hsl(uv.x * 5.0 + iTime, 1.0, 0.5);

    float timeFactor = iTime * 0.5;
    float mouseFactor = turbulent(vec2(uv.x, uv.y));

    color += sin(timeFactor + uv.x * 10.0) * 0.3;
    color += sin(timeFactor + uv.y * 10.0 + PI/2.0) * 0.3;

    vec3 rgbLines = hsl2rgb(vec3(
        sin(uv.x * 5.0 + timeFactor) * 0.5 + 0.5,
        sin(uv.y * 5.0 + timeFactor + PI/2.0) * 0.5 + 0.5,
        1.0
    ));

    color = mix(color, vec3(1.0), rgbLines);

    return vec4(color, 1.0);
}
