vec4 Effect2(vec2 uv) {
    // Create rotating pattern by combining sine waves and turbulence
    float t = iTime * 0.5;
    vec2 st = uv - 0.5;

    // Add rotation effect
    st.x += sin(iTime) * 0.3;
    st.y += cos(iTime) * 0.3;

    // Create stripes pattern with turbulence
    float pattern = sin(uv.x * 10.0 + t) * 0.5 + 0.5;
    pattern += turbulent(vec2(st.x * 4.0, st.y * 4.0)) * 0.3;

    // Convert to HSL for smooth color transitions
    vec3 hslColor = vec3(pattern * 360.0, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
