vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Calculate radius and angle
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add rotation based on time
    theta += iTime * 0.5;

    // Create pulsating radial lines
    int n = 16; // Number of lines
    float pulse = sin(theta * float(n) + iTime * 2.0) * 0.5 + 0.5;

    // Create color wheel effect using HSL
    vec3 hslColor = vec3(
        theta / (2.0 * PI), // Hue based on angle
        1.0,               // Saturation
        pulse              // Lightness modulated by pulsation
    );

    // Convert to RGB and multiply by radius for fading effect
    vec3 color = hsl2rgb(hslColor) * (1.0 - r * 0.5);

    return vec4(color, 1.0);
}
