vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float a = atan2(st.y, st.x);
    float r = length(st);

    // Create pulsating effect using time
    float pulse = sin(iTime * 0.5 + r * 2.0) * 0.5 + 0.5;
    float radiusPulse = r * (1.0 + pulse * 0.3);

    // Create spiral pattern with color cycling
    float angleOffset = iTime * 0.2;
    float hue = fract((a / PI + angleOffset) * 0.5);

    // Add noise for extra complexity
    vec2 noiseUV = uv * 10.0 + iTime;
    float noiseValue = turbulent(noiseUV) * 0.3;

    // Combine all elements into final color
    vec3 hslColor = vec3(hue, 1.0, 0.5 + noiseValue);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
