vec4 Effect2(vec2 uv) {
    float t = iTime * 0.5;

    // Create base color from time and position
    vec3 color = vec3(
        sin(t + uv.x * 10.0),
        sin(t + uv.y * 10.0 + 2.0),
        sin(t + 3.0)
    );

    // Add noise and turbulence for more complexity
    float n = noise(vec2(sin(t), uv.y));
    float turb = turbulent(uv * 5.0);

    // Combine elements with HSL conversion
    vec3 hslColor = vec3(
        sin(t + uv.x) * 0.5 + 0.5,
        abs(n) * 0.7 + 0.3,
        turb * 0.6 + 0.4
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
