vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float t = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Add noise and turbulence
    float noiseValue = turbulent(rotatedUv * 10.0) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(t * 0.2 + noiseValue),
        0.7,
        0.6
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add shape elements
    float distanceFromCenter = length(rotatedUv);
    float distanceFromCorner = length(abs(rotatedUv) - 1.0);
    float shapeValue = sin(distanceFromCenter * 8.0 + t) * cos(distanceFromCorner * 4.0 + t);

    // Combine elements
    vec3 finalColor = rgbColor * (noiseValue + shapeValue * 0.5);
    finalColor *= sin(t * 2.0 + noiseValue * pi) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
