vec4 Effect2(vec2 uv) {
    vec3 p = vec3(uv * 10.0 + iTime, iTime);
    float n = noise(p);
    float t = turbulent(p);

    float speed = 0.5;
    float time = iTime * speed;

    vec3 color1 = hsl(0.5 + sin(time) * 0.2, 0.8, 0.6);
    vec3 color2 = hsl(0.3 + cos(time) * 0.2, 0.7, 0.5);

    float mixAmount = fract(sin(time * 0.5) + t * 0.5);
    vec3 finalColor = mix(color1, color2, mixAmount);

    return vec4(hsv2rgb(finalColor), 1.0);
}
