vec4 Effect2(vec2 uv) {
    vec2 st = uv * 2.0 - 1.0;
    float time = mod(iTime, 1.0);

    // Create a dynamic color palette using HSL
    vec3 colorA = hsl(time * 0.5 + 0.5, 0.8, 0.7);
    vec3 colorB = hsl(time * 0.5, 0.8, 0.7);

    // Generate noise pattern with time offset
    float n1 = noise(st + time * 0.25);
    float n2 = noise(vec2(fract(st.x * 1.3), fract(st.y * 1.7)) + time * 0.3);

    // Combine noise values for more complex pattern
    float noiseValue = (n1 + n2) * 0.5;

    // Calculate final color using interpolated palette
    vec3 finalColor = mix(colorA, colorB, noiseValue);

    return vec4(finalColor, 1.0);
}
