vec4 Effect2(vec2 uv) {
    // Scale UV coordinates by a factor that changes with time
    vec2 st = uv * 10.0 + iTime;

    // Create grid cells using integer division
    vec2 cell = floor(st);

    // Calculate pulsation values for each cell
    float pulse = sin(iTime * 0.5 + cell.x * pi) * 0.5 + 0.5;
    pulse += sin(iTime * 0.7 + cell.y * pi) * 0.3;

    // Add noise to create organic movement
    vec2 noiseSt = st + iTime;
    float noiseVal = turbulent(noiseSt);
    pulse += noiseVal * 0.2;

    // Create color variations using HSL
    vec3 hslColor = vec3(
        (cell.x + cell.y) * 0.1 + iTime * 0.5,
        0.7 + pulse * 0.3,
        0.5 + noiseVal * 0.2
    );

    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
