vec4 Effect2(vec2 uv) {
    // Create a base wave pattern using time and position
    float wave = sin(uv.x * 10.0 + iTime) * cos(uv.y * 10.0 + iTime);

    // Add noise to create more dynamic movement
    vec3 noisePoint = vec3(uv * 5.0, iTime * 2.0);
    float turbulence = turbulent(noisePoint);

    // Combine wave and noise for final pattern
    float pattern = (wave + turbulence) * 0.5 + 0.5;

    // Create smooth color transitions using HSL
    vec3 hslColor = vec3(
        fract((uv.x + uv.y) * 0.5 + iTime * 0.1),  // Hue variation across screen and time
        0.7,  // Saturation
        pattern  // Lightness varies with the wave pattern
    );

    // Convert to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
