vec4 Effect2(vec2 uv) {
    // Create base grid pattern using sine waves
    vec2 st = uv * 10.0;
    float grid = sin(st.x + iTime) * sin(st.y + iTime);

    // Add noise and turbulence for dynamic effect
    vec3 noisePos = vec3(uv * 5.0, iTime * 0.1);
    float turb = turbulent(noisePos);

    // Combine grid pattern with noise/turbulence
    grid += turb * 0.5;

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(iTime * 2.0, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Mix colors based on grid pattern
    vec3 finalColor = mix(vec3(0.1), rgbColor, abs(grid));

    return vec4(finalColor, 1.0);
}
