vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add rotation over time

    // Create pulsating RGB shifts using sine functions with different phases
    vec3 color = vec3(
        sin(theta + iTime) * 0.5 + 0.5,
        sin(theta + iTime * 0.7 + 2.0) * 0.5 + 0.5,
        sin(theta + iTime * 0.9 + 4.0) * 0.5 + 0.5
    );

    // Add noise and turbulence for more dynamic effect
    float noiseValue = noise(vec2(uv.x, uv.y) * 10.0 + iTime);
    color += noiseValue * 0.3;

    // Create radial gradient effect
    color *= r * 0.5 + 0.5;

    return vec4(color, 1.0);
}
