vec4 Effect2(vec2 uv) {
    // Remap UV to (-1,-1) to (1,1) range
    vec2 st = uv * 2.0 - 1.0;

    // Add rotation based on time
    float angle = iTime * 0.5;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rotMatrix * st;

    // Convert to polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create color components with sine waves and different offsets
    vec3 color = vec3(
        sin(r * 10.0 + iTime) + 0.5,
        sin(r * 10.0 + iTime + 2.0) + 0.5, 
        sin(r * 10.0 + iTime - 2.0) + 0.5
    );

    // Add some turbulence for extra detail
    color += vec3(turbulent(st * 4.0)) * 0.2;

    return vec4(color, 1.0);
}
