vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float angle = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(angle)*uv.x - sin(angle)*uv.y,
        sin(angle)*uv.x + cos(angle)*uv.y
    );

    // Create sine wave patterns with time offset
    float waveX = sin(rotatedUv.x * 4.0 + iTime) * 0.5 + 0.5;
    float waveY = sin(rotatedUv.y * 4.0 - iTime) * 0.5 + 0.5;

    // Add turbulence for dynamic pattern
    vec2 noiseUV = rotatedUv * 0.5 + 0.5;
    float turb = turbulent(noiseUV);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1, 1.0),  // Hue changes over time
        0.7,                    // Saturation
        0.5 + turb * 0.5        // Brightness varies with turbulence
    );

    // Convert HSL to RGB and mix with wave patterns
    vec3 rgbColor = hsl2rgb(hslColor);
    vec3 finalColor = rgbColor * vec3(waveX, waveY, 1.0) * (0.5 + turb * 0.5);

    return vec4(finalColor, 1.0);
}
