vec4 Effect2(vec2 uv) {
    vec4 result;

    // Create polar coordinates from UV
    float dx = uv.x - 0.5;
    float dy = uv.y - 0.5;
    float theta = atan2(dy, dx);
    float r = length(vec2(dx, dy)) * 10.0;

    // Add time-based rotation and create multiple layers
    theta += sin(iTime * 0.5 + theta) * 0.5;
    theta += sin(theta * 3.0 + iTime) * 0.3;
    theta += sin(theta * 5.0 - iTime * 2.0) * 0.2;

    // Create base pattern with noise
    float pattern = sin(r + iTime) * 0.5 + 0.5;
    pattern *= sin(theta * 4.0 + iTime * 2.0);
    pattern += turbulent(uv * 10.0 + iTime) * 0.3;

    // Create color variations
    vec3 color = hsl(
        theta * 0.5 + iTime * 0.1,   // Hue varies with angle and time
        r * 0.2 + 0.5,              // Saturation increases with radius
        pattern * 0.6 + 0.4         // Lightness varies with pattern
    );

    result = vec4(color, 1.0);
    return result;
}
