vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to [-1, 1] range and center them
    vec2 st = uv * 2.0 - 1.0;

    // Add rotation based on time
    float angle = iTime * 0.5;
    vec2 rotated = vec2(
        cos(angle) * st.x - sin(angle) * st.y,
        sin(angle) * st.x + cos(angle) * st.y
    );

    // Create a plasma effect using noise and turbulence
    float noise1 = turbulent(rotated * 4.0);
    float noise2 = turbulent(vec3(rotated * 8.0, iTime));
    float noise3 = turbulent(rotated * 16.0 + vec2(iTime));

    // Combine noises for color components
    float r = noise1 * 0.5 + 0.5;
    float g = noise2 * 0.5 + 0.5;
    float b = noise3 * 0.5 + 0.5;

    // Create smooth color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        (r + g + b) * 180.0,   // Hue based on combined noises
        0.7,                   // Saturation
        0.5                    // Lightness
    ));

    // Add some movement to the colors
    color *= hsl2rgb(vec3(
        iTime * 60.0 + noise1 * 360.0,
        0.5,
        0.5
    )) * 0.5;

    return vec4(color, 1.0);
}
