vec4 Effect2(vec2 uv) {
    // Animate based on time
    float t = iTime * 0.5;

    // Generate noise value with time offset
    float n = noise(uv + vec2(sin(t), cos(t)));

    // Create base color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        sin(t * 0.7) * 0.5 + 0.5,   // Hue
        1.0,                       // Saturation
        0.6                        // Lightness
    ));

    // Add flowing sine wave patterns
    float pattern = sin(uv.x * 8.0 + t) * 0.5 +
                   sin(uv.y * 4.0 + t * 0.7) * 0.3 +
                   sin(length(uv) * 2.0 - t) * 0.2;

    // Combine noise and pattern with color
    vec3 finalColor = color * (abs(n * 0.5 + 0.5)) *
                     (sin(pattern * pi) * 0.5 + 0.5);

    // Add brightness and contrast
    finalColor = pow(finalColor, vec3(2.2));
    finalColor = mix(vec3(0.1), finalColor, 0.7);

    return vec4(finalColor, 1.0);
}
