vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create rotating sine wave pattern
    float sa = sin(a + iTime * 3.0);
    float ca = cos(a + iTime * 3.0);

    // Add turbulence and noise
    float noiseVal = turbulent(vec2(r, a)) * 0.5 + 0.5;
    float pattern = sin(r * 10.0 + iTime) * noiseVal * (sa * ca * 4.0);

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(a * 2.0 + iTime, 1.0), // Hue variation
        0.7 + noiseVal * 0.3,      // Saturation
        0.5 + pattern * 0.5       // Brightness modulation
    ));

    return vec4(color, 1.0);
}
