vec4 Effect2(vec2 uv) {
    // Scale and offset UV coordinates for pattern size
    vec2 st = uv * 5.0 + iTime;

    // Create a noise-based pattern using multiple octaves
    float n1 = noise(st);
    float n2 = noise(st + vec2(1.3, 4.7) + iTime*0.5);
    float pattern = sin(n1 * 3.0 + n2) * 0.5 + 0.5;

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        fract(pattern + iTime/2.0),   // Hue variation over time and space
        0.7,                          // Saturation
        0.6                           // Lightness
    );

    // Convert HSL to RGB
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add some dynamic color variations using noise
    vec3 colorVariation = vec3(
        noise(vec3(st.x + iTime, st.y, 0.0)) * 0.5 + 0.5,
        noise(vec3(st.x - iTime, st.y + 1.0, 0.0)) * 0.5 + 0.5,
        noise(vec3(st.x + st.y + iTime*2.0, 0.0, 0.0)) * 0.5 + 0.5
    );

    // Combine colors and add some pulsing effect
    vec3 finalColor = rgbColor * colorVariation;
    finalColor *= sin(iTime) * 0.5 + 0.75; // Add global pulsing

    return vec4(finalColor, 1.0);
}
