vec4 Effect2(vec2 uv) {
    float speed = 0.5;
    float freq1 = 3.0;
    float freq2 = 6.0;

    // Compute base rotation frequencies
    float rot1 = sin(iTime * speed + uv.x * freq1) * 0.5 + 0.5;
    float rot2 = cos(iTime * speed * 1.5 + uv.y * freq2) * 0.5 + 0.5;

    // Compute color layers with noise and turbulence
    vec3 color;
    color.r = sin(uv.x * freq1 + iTime * speed) * 0.6 +
              turbulent(vec2(uv.x * 2.0, uv.y * 2.0 + iTime)) * 0.4;

    color.g = sin(uv.y * freq2 + iTime * speed * 1.3) * 0.6 +
              turbulent(vec2(uv.x * 1.5, uv.y * 1.5 - iTime)) * 0.4;

    color.b = cos(uv.x * freq1 + uv.y * freq2 + iTime * speed * 1.7) * 0.6 +
              turbulent(vec2(uv.x * 3.0, uv.y * 3.0 + iTime)) * 0.4;

    // Normalize and mix colors
    color = (color * 0.5 + 0.5);
    color = vec3(
        color.r * rot1 + color.g * (1.0 - rot2),
        color.g * rot2 + color.b * (1.0 - rot1),
        color.b * rot1 + color.r * (1.0 - rot2)
    );

    // Add brightness variation
    float brightness = sin(iTime * 0.7) * 0.5 + 0.5;
    color *= brightness;

    return vec4(pow(color, vec3(1.0)), 1.0);
}
