vec4 Effect2(vec2 uv) {
    // Scale and rotate coordinates for shifting effect
    vec2 st = uv * 5.0 + iTime;
    st.x *= cos(iTime * 0.5);
    st.y *= sin(iTime * 0.5);
    // Create distance field from lines
    float d1 = abs(fract(sin(st.x * 3.0) * 0.5 + 0.5) - 0.5) * 2.0;
    float d2 = abs(fract(sin(st.y * 3.0) * 0.5 + 0.5) - 0.5) * 2.0;

    // Combine distances and add time-based variation
    float d = min(d1, d2);
    d *= sin(iTime * 0.5 + st.x * 3.0) * 0.5 + 0.5;
    d += turbulent(st * 0.5) * 0.3;
    // Convert to color using HSL
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.1 + st.x * 0.1) * 0.5 + 0.5,   // Hue variation
        0.7,                                         // Saturation
        max(0.0, 1.0 - d * 2.0)                       // Value based on distance
    ));
    return vec4(color, 1.0);
}
