vec4 Effect2(vec2 uv) {
    vec4 fragColor;

    // Create a time-based animation factor
    float time = iTime * 0.5;

    // Scale UV coordinates and add time offset for movement
    vec2 st = uv * 50.0 + vec2(time, time);

    // Add rotation to create circular motion
    float angle = sin(time) * 0.5 + 1.0;
    st = mat2(cos(angle), -sin(angle),
              sin(angle), cos(angle)) * st;

    // Generate turbulence values at different scales
    float t1 = turbulent(st * 0.5);
    float t2 = turbulent(vec3(st, time * 2.0));

    // Combine turbulence values for color shifting
    float mixVal = (t1 + t2) * 0.5;

    // Create a dynamic color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (mixVal + time) * 0.4,   // Hue
        0.7 + mixVal * 0.3,     // Saturation
        0.5 + sin(time) * 0.3   // Lightness
    ));

    fragColor = vec4(color, 1.0);
    return fragColor;
}
