vec4 Effect2(vec2 uv) {
    // Create a base color using HSL
    vec3 base = hsl2rgb(vec3(0.5 * sin(iTime * 0.5 + uv.x * pi), 1.0, 0.5));

    // Add turbulent noise pattern
    float t = iTime * 0.1;
    vec3 noisePattern = vec3(
        turbulent(uv * 10.0 + t),
        turbulent(uv * 20.0 + t * 0.5),
        turbulent(uv * 30.0 + t * 0.7)
    );

    // Pulse each color channel at different frequencies
    vec3 color = base + noisePattern;
    color.r *= sin(iTime * 1.0 + uv.x * pi) * 0.5 + 0.5;
    color.g *= sin(iTime * 1.3 + uv.y * pi) * 0.5 + 0.5;
    color.b *= sin(iTime * 1.6 + (uv.x + uv.y) * pi) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
