vec4 Effect2(vec2 uv) {
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Create multiple rotating wheels with different scales and speeds
    for(float scale = 0.5; scale <= 1.0; scale += 0.3) {
        vec2 rotatedSt = vec2(
            cos(angle * scale + iTime * scale * 0.5) * radius,
            sin(angle * scale + iTime * scale * 0.7) * radius
        );

        // Add some turbulence to create interesting patterns
        float turb = turbulent(rotatedSt * 10.0);
        radius += turb * 0.1;

        // Create color wheels with shifting hues
        vec3 color = hsl2rgb(vec3(
            (iTime + length(st)) * 0.5,
            0.7,
            0.6
        ));

        // Mix colors based on distance from center
        float mixAmount = smoothstep(1.0, 0.0, radius);
        color = mix(vec3(0.0), color, mixAmount);

        // Add to final color
        gl_FragColor += vec4(color, 1.0) * mixAmount;
    }

    return gl_FragColor;
}
