vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time-based offset
    vec2 st = uv * 10.0 + sin(iTime) * 5.0;

    // Create two different wave patterns with varying frequencies
    float waveA = sin(st.x * 0.5 + iTime) * cos(st.y * 0.5);
    float waveB = cos(st.x * 0.7 - iTime) * sin(st.y * 0.3);

    // Combine waves to create interference pattern
    float interference = (waveA + waveB) * 0.5;

    // Create dynamic color shifts using HSL
    vec3 baseColor = hsl(interference * 0.5 + 0.5, 1.0, 0.6);
    vec3 secondaryColor = hsl((interference + sin(iTime)) * 0.5 + 0.5, 1.0, 0.4);

    // Mix colors based on interference pattern
    vec3 finalColor = mix(baseColor, secondaryColor, abs(interference));

    // Add noise for texture and depth
    float noiseValue = noise(st * 0.2 + iTime) * 0.5 + 0.5;
    finalColor *= noiseValue;

    return vec4(finalColor, noiseValue);
}
