vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Scale coordinates for more detailed pattern
    vec2 st = uv * 10.0;

    // Add time-based movement
    float time = iTime * 0.5;

    // Create shifting pattern using turbulence
    float t = turbulent(vec3(st + time, time));

    // Use HSL color with varying hue and lightness
    vec3 hslColor = vec3(
        mod(time * 2.0, 1.0),  // Hue
        0.7,                  // Saturation
        0.5 + t * 0.5         // Lightness
    );

    // Convert to RGB and output
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
