vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create three rotating circles with different phase offsets
    float t = iTime * 0.5;

    // Circle 1 (Red)
    vec2 offset1 = vec2(sin(t) * 0.3, cos(t) * 0.3);
    vec2 pos1 = uv + offset1;
    float d1 = length(pos1 - 0.5);
    float circle1 = smoothstep(0.4, 0.3, r - theta * 0.1);
    // Circle 2 (Green)
    vec2 offset2 = vec2(sin(t + 2.094) * 0.3, cos(t + 2.094) * 0.3);
    vec2 pos2 = uv + offset2;
    float d2 = length(pos2 - 0.5);
    float circle2 = smoothstep(0.4, 0.3, r - theta * 0.1);
    // Circle 3 (Blue)
    vec2 offset3 = vec2(sin(t + 4.188) * 0.3, cos(t + 4.188) * 0.3);
    vec2 pos3 = uv + offset3;
    float d3 = length(pos3 - 0.5);
    float circle3 = smoothstep(0.4, 0.3, r - theta * 0.1);
    // Combine circles with increasing intensity
    vec3 color = vec3(
        circle1,
        circle2,
        circle3
    ) * (sin(iTime * 0.5) * 0.5 + 0.5);
    // Add some noise and turbulence for extra effect
    float noiseVal = noise(uv * 4.0 + iTime);
    color *= 1.0 + noiseVal * 0.2;
    return vec4(color, 1.0);
}
