vec4 Effect2(vec2 uv) {
    // Create a base color using sine functions for hue and lightness
    vec3 cx = sin(vec3(iTime * 2.0 + uv.x * 10.0, iTime * 3.0 + uv.y * 10.0, iTime * 4.0));
    vec3 cy = sin(vec3(uv.x * 5.0 + iTime * 6.0, uv.y * 7.0 + iTime * 8.0, iTime * 9.0));

    // Combine colors with turbulence
    vec3 color = hsl2rgb(vec3(
        sin(iTime) * 0.5 + 0.5,
        sin(uv.x * 10.0 + iTime * 2.0) * 0.5 + 0.5,
        sin(uv.y * 10.0 + iTime * 3.0) * 0.5 + 0.5
    ));

    // Add turbulence to create dynamic shifts
    float t = turbulent(vec3(uv.x, uv.y, iTime));
    color += vec3(sin(iTime + t), sin(iTime + t + 2.0), sin(iTime + t + 4.0)) * 0.5;

    return vec4(color, 1.0);
}
