vec4 Effect2(vec2 uv) {
    // Create a position that moves over time
    vec3 pos = vec3(uv * 5.0 + iTime, iTime);

    // Add some noise and turbulence to create dynamic patterns
    float t1 = turbulent(pos);
    float t2 = turbulent(pos + vec3(1.0, 0.7, 0.3) * 2.0);

    // Create a color transition effect using HSL colors
    vec3 color1 = hsl(t1 * 0.5 + 0.5, 0.8, 0.6);
    vec3 color2 = hsl((t2 * 0.5 + 0.5) + sin(iTime * 0.5), 0.8, 0.6);

    // Mix the colors based on position and time
    float mixAmount = turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    return vec4(finalColor, 1.0);
}
