vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 center = uv - 0.5;
    float radius = length(center);
    float angle = atan2(center.y, center.x);
    // Add time-based rotation
    angle += iTime * 0.5;
    // Create swirling pattern by mixing radius and angle
    float swirl = radius * 10.0 + angle * 0.5;

    // Use noise to add texture
    vec2 noiseUV = uv * 10.0 + iTime;
    float n = noise(noiseUV) * 0.3;
    // Create rainbow hues using HSL color space
    vec3 color = hsl2rgb(vec3(
        (angle * 0.5 + swirl * 0.1 + n) * 0.1,
        1.0 - radius * 0.8,
        0.5 + n * 0.2
    ));
    // Add central focal point
    color *= step(0.3, 1.0 - radius);
    return vec4(color, 1.0);
}
