vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create hexagonal grid pattern using polar coordinates
    float angle = theta + iTime * 0.1;
    float cell = mod(r * 5.0 + sin(angle) * 0.5, 1.0);

    // Add noise and turbulence for glowing effect
    vec3 noisePos = vec3(st.x, st.y, iTime * 0.2);
    float t = turbulent(noisePos);
    float n = noise(vec2(r * 0.5 + angle));

    // Create pulsing animation
    float pulse = sin(iTime * 0.5) * 0.3;
    cell += (turbulent(vec2(r * 0.7, angle)) + t) * 0.4;
    cell += n * 0.2;

    // Generate color based on hexagonal pattern
    vec3 baseColor = hsl(cell * 0.6 + 0.3, 0.8, 0.5);
    vec3 glowColor = mix(vec3(1.0), baseColor, cell * 0.4);

    // Combine colors and add pulsing effect
    vec3 finalColor = mix(baseColor, glowColor, smoothstep(0.7, 1.0, cell + pulse));

    return vec4(finalColor, 1.0);
}
