vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) + iTime * 0.5; // Add rotation over time

    // Create color wheel effect using HSL colors
    vec3 hslColor;
    hslColor.x = fract(theta / (2.0 * pi)); // Hue based on angle
    hslColor.y = 1.0 - r * 0.5;             // Saturation decreases with radius
    hslColor.z = 0.5 + turbulent(vec3(theta, r, iTime)) * 0.2; // Lightness with noise

    // Convert HSL to RGB and return
    return vec4(hsl2rgb(hslColor), 1.0);
}
