vec4 Effect2(vec2 uv) {
    // Create oscillating lattice points
    vec2 st = uv * 10.0;
    vec2 center = floor(st) + 0.5;

    // Add time-based oscillation to centers
    float t = iTime * 0.5;
    center.x += sin(t + center.y * 3.14) * 0.3;
    center.y += cos(t + center.x * 3.14) * 0.3;

    // Calculate vector from current point to lattice center
    vec2 pos = st - center;
    float dist = length(pos);
    float angle = atan2(pos.y, pos.x);

    // Create color based on distance and angle
    vec3 color = hsl2rgb(vec3(
        mod(angle * 0.5 + t, 1.0),
        0.7,
        0.6
    ));

    // Add noise variation
    float n = noise(center.xy * 0.1);
    color *= mix(1.0, 0.5, smoothstep(0.2, 0.8, dist));

    return vec4(color, 1.0);
}
