vec4 Effect2(vec2 uv) {
    // Normalize UV coordinates to -1..1 range
    vec2 st = uv * 2.0 - 1.0;

    // Create polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create circular pattern with noise and turbulence
    float pattern = turbulent(vec3(st * 4.0, iTime)) * 0.5 + 0.5;
    pattern += sin(r * 10.0 + iTime) * 0.5;

    // Mix radius and angle for color variation
    vec3 color = hsl2rgb(vec3(
        (r * 0.5 + 0.5 + iTime * 0.1) * 360.0, // Hue
        0.7, // Saturation
        0.5 + sin(a * 4.0 + iTime * 2.0) * 0.3 // Lightness
    ));

    // Scale and combine patterns
    pattern = (pattern + r * 0.5) * 0.7;
    color *= pattern;

    return vec4(color, 1.0);
}
