vec4 Effect2(vec2 uv) {
    // Create a time-based oscillation factor
    float t = sin(iTime * pi) * 0.5 + 0.5;

    // Combine position and time with some scaling
    vec3 colorHSL = vec3(
        sin(uv.x * 10.0 + iTime) * 0.5 + 0.5,   // Hue variation based on x position and time
        0.7,                                      // Saturation
        0.8                                       // Lightness
    );

    // Add some turbulent noise to create interesting patterns
    float noiseValue = turbulent(uv * 10.0 + vec2(iTime)) * 0.3;

    // Convert HSL to RGB and mix with noise
    vec3 finalColor = hsl2rgb(colorHSL) * (sin(t * pi) * 0.5 + 0.5 + noiseValue);

    return vec4(finalColor, 1.0);
}
