vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5;

    // Create hexagonal grid pattern
    vec2 grid = vec2(r * cos(a), r * sin(a));
    grid += noise(vec3(grid, iTime)) * 0.5;

    // Add turbulence for ethereal effect
    float turb = turbulent(grid) * 0.5 + 0.5;
    turb *= sin(iTime * 0.2 + a * 2.0);

    // Create vibrant color palette
    vec3 color1 = hsl(a * 0.1 + iTime, 1.0, 0.5);
    vec3 color2 = hsl(a * 0.1 + iTime + 2.0, 1.0, 0.5);

    // Combine colors with turbulence
    vec3 finalColor = mix(color1, color2, turb);

    // Add fading trail effect
    float fade = sin(iTime - r * 2.0) * 0.5 + 0.5;
    finalColor *= fade;

    return vec4(finalColor, 1.0);
}
