vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create pulsing effect using noise and turbulence
    float pulse = turbulent(vec3(uv * 5.0, iTime)) * 0.5 + 0.5;
    pulse = sin(iTime * 2.0 + theta) * 0.5 + 0.5;

    // Create color wheel with HSL colors
    vec3 hslColor = vec3(theta * 0.1, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine pulsing effect with color wheel
    float ring = smoothstep(0.8, 1.0, r + pulse * 0.2);
    vec3 finalColor = mix(rgbColor, vec3(1.0), ring);

    return vec4(finalColor, 1.0);
}
