vec4 Effect2(vec2 uv) {
    // Create a sine wave pattern with time-based offset
    float s = sin(uv.x * 50.0 + iTime) * 0.5;
    s += sin(uv.y * 50.0 + iTime * 0.5) * 0.5;

    // Add some turbulence for dynamic movement
    float t = turbulent(uv * 10.0 + vec2(iTime)) * 0.5;

    // Create a color based on the sine wave value
    vec3 col = hsl(s * 0.5 + iTime * 0.2, 1.0, 0.5);

    // Mix in turbulence for added detail
    col += t;

    return vec4(col, 1.0);
}
