vec4 Effect2(vec2 uv) {
    // Rotate coordinates with time
    float t = iTime * 0.5;
    vec2 rotated = uv * mat2(cos(t), sin(-t), sin(t), cos(t));

    // Add noise and sine variation
    vec2 st = rotated * 10.0 + vec2(sin(iTime) * 3.0, cos(iTime) * 3.0);
    float n = noise(st * 0.5 + iTime);

    // Create color variations using HSL
    vec3 color;
    color.x = sin(rotated.x * 10.0 + t) * 0.5 + 0.5;
    color.y = sin(rotated.y * 10.0 + t * 2.0) * 0.5 + 0.5;
    color.z = n * 0.5 + 0.5;

    // Convert to RGB and output
    return vec4(hsl2rgb(vec3(color.x, color.y, color.z)), 1.0);
}
