vec4 Effect2(vec2 uv) {
    // Create rotating color gradient base
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime * 0.5;

    // Add undulating sine wave pattern
    vec2 distortedUV = uv + sin(uv * 4.0 + iTime) * 0.1;
    distortedUV += sin(distortedUV * 6.0 - iTime) * 0.05;

    // Combine color transformations
    vec3 colorBase = hsl2rgb(vec3(angle * 0.1, 0.7, 0.5));
    float noiseValue = noise(vec3(uv * 4.0 + iTime, 0.0)) * 0.5 + 0.5;

    // Create final color with dynamic patterns
    vec3 finalColor = mix(
        hsl2rgb(vec3(angle * 0.1 + noiseValue * 0.2, 0.7, 0.6)),
        colorBase,
        radius * 0.5 + 0.5
    );

    // Add undulating effect to the final color
    finalColor *= sin(radius * 8.0 - iTime) * 0.3 + 0.7;

    return vec4(finalColor, 1.0);
}
