vec4 Effect2(vec2 uv) {
    // Animate coordinates over time
    vec2 st = uv * 5.0 + vec2(sin(iTime)*3.0, cos(iTime*0.7)*2.0);

    // Create a noise pattern with turbulence
    float noise1 = turbulent(st + sin(iTime) * 2.0);
    float noise2 = turbulent(st + vec2(1.5, 2.3) + iTime*0.5);

    // Create color shifts using sine functions
    vec3 color = hsl(
        sin(uv.x * 8.0 + iTime) * 0.5 + 0.5,
        sin(uv.y * 6.0 + iTime*0.7) * 0.5 + 0.5,
        0.5
    );

    // Convert HSL to RGB and mix with noise patterns
    color = hsl2rgb(color);
    color *= (noise1 * 0.5 + 0.5);
    color += hsv2rgb(vec3(sin(iTime*0.3)*0.5 + 0.5, 1.0, noise2 * 0.5 + 0.5)) * 0.4;

    return vec4(color, 1.0);
}
