vec4 Effect2(vec2 uv) {
    // Create position based on time and UV coordinates
    vec2 pos = uv + 0.5 * sin(iTime + uv.x * 10.0);

    // Convert to polar coordinates
    float r = length(pos);
    float theta = atan2(pos.y, pos.x) + sin(iTime) * 0.5;

    // Create shifting lines using sine functions
    float line = abs(sin(theta * 4.0 + iTime));
    line = step(0.1, line) * (1.0 - smoothstep(0.9, 1.0, line));

    // Add noise for variation
    float noiseVal = noise(vec2(r, theta)) * 0.5;
    line += noiseVal;

    // Create color based on HSL values
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.1 + r) * 0.5 + 0.5,
        0.7 + noiseVal * 0.3,
        0.8 - line * 0.4
    ));

    return vec4(color, 1.0);
}
