vec4 Effect2(vec2 uv) {
    // Create rotating polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x) + iTime * 0.5;
    float radius = length(st);

    // Add noise and turbulence
    float n = noise(vec3(uv * 4.0, iTime));
    float t = turbulent(vec2(angle, radius * 0.5));

    // Create color shifts using HSL
    vec3 color = hsl(
        sin(iTime + angle) * 0.5 + 0.5,
        0.7 + n * 0.3,
        0.5 + t * 0.5
    );

    // Convert to RGB and mix with positional gradients
    vec3 finalColor = hsl2rgb(color);
    finalColor *= sin(radius * 8.0 + iTime) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
