vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 offset = uv - 0.5;
    float radius = length(offset);
    float angle = atan2(offset.y, offset.x);

    // Create a pattern of lines radiating from the center
    float segments = 32.0;
    float linePosition = fract(angle * segments / (pi * 2.0) + sin(iTime * 0.5));

    // Calculate thickness based on distance from center
    float thickness = (1.0 - radius * radius) * 0.5 + 0.5;
    thickness *= sin(iTime + radius * 4.0) * 0.5 + 0.5;

    // Create color variation based on angle and radius
    float hue = angle / (pi * 2.0) * 360.0 + sin(iTime * 2.0) * 180.0;
    hue = fract(hue / 360.0);

    // Create the final color pattern
    vec3 color = vec3(0.0);
    if (abs(linePosition - 0.5) < 0.1) {
        color = hsl2rgb(vec3(hue, 1.0, thickness * 0.7 + 0.3));
    }

    return vec4(color, 1.0);
}
