vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add rotation over time

    // Create hexagonal divisions using sine wave
    float segments = sin(6.0 * theta) * 0.5 + 0.5;

    // Smooth transitions between colors using HSL
    vec3 color = hsl2rgb(vec3(
        mod(theta * 10.0 + iTime, 2.0 * PI) / (2.0 * PI), // Hue based on angle and time
        0.7, // Saturation
        0.5 // Lightness
    ));

    // Add noise for texture
    float noiseValue = turbulent(vec3(uv * 10.0, iTime)) * 0.2;
    color += noiseValue;

    return vec4(color, 1.0);
}
